}

for (i = 0; i < n; i++) {
    for (j = 0; j < n; j++) {
        // constant work
    }
}
# Runtime: O(n^2)

for (i = 0; i < n; i++) {
    // work
}

for (j = 0; j < m; j++) {
    // work
}
# Runtime: O(n + m)

for (i = 0; i < n; i++) {
    for (j = 0; j < i; j++) {
        // work
    }
}
# Total iterations = 1 + 2 + 3 + ... + (n-1)
# = n(n-1)/2 → O(n^2)

for (i = 1; i < n; i = i * 2) {
    // work
}
# i = 1, 2, 4, 8, ..., n
# Runs log₂(n) times → O(log n)

while (n > 1) {
    n = n / 2;
}
# Also O(log n)

for (i = 0; i < n; i++) {
    for (j = 1; j < n; j *= 2) {
        // work
    }
}
# Outer: n
# Inner: log n
# Total: n * log n → O(n log n)

for (i = 0; i < n; i += 3) {
    // work
}
# Still O(n) because constants don't matter

for (i = 0; i < n; i++) {
    for (j = 0; j < i * i; j++) {
        // work
    }
}
# Total work = sum of i^2 from 1 to n → O(n^3)

for (i = 0; i < n; i++) {      // O(n)
    for (j = i; j < n; j *= 2) {  // O(log n)
        // work
    }
}
# Total = O(n log n)
