/* Example: Kebab-case for class names */
.header-section {
    /* styles */
}

/* Example: CamelCase for IDs */
<div id="mainContent"></div>

/* Header Styles */
header {
    background-color: #333;
    color: white;
    text-align: center;
}

/* Main Content Styles */
main {
    padding: 20px;
}

/* Footer Styles */
footer {
    background-color: #333;
    color: white;
    text-align: center;
    padding: 10px;
}

/* Mobile-first styles */
body {
    font-size: 16px;
}

/* Larger screens */
@media (min-width: 600px) {
    body {
        font-size: 18px;
    }
}

/* This function calculates the sum of two numbers */
function sum(a, b) {
    return a + b;
}
