def make_sound(animal):
    if animal == 'dog':
        print('Woof!')
    elif animal == 'cat':
        print('Meow!')
    elif animal == 'cow':
        print('Moo!')
    else:
        print('Unknown animal sound')

class Dog:
    def __init__(self, name, age):
        self.name = name
        self.age = age

    def bark(self):
        print(f'{self.name} says woof!')
